import React, { useState, useEffect } from 'react'
import { Link ,useHistory} from 'react-router-dom'
import { FiPower, FiTrash2 } from 'react-icons/fi'


import api from '../../services/api';

import './styles.css'

import logoImg from '../../assets/logo.svg'

export default function Profile() {

  const logof = useHistory();

  const [incidents, setincidents] = useState ([]); // fazer a troca dos valores , arrary

  const ongName = localStorage.getItem('ongName')
  const ongId = localStorage.getItem('ongId')

  useEffect(()=> { // uma funcao que chama a api
    api.get('profile',{ // chamar api com rota
      headers:{
        Authorization: ongId, // passando para a api a autorizacao com base no header
      }
    }).then(response => {
      setincidents(response.data) // guardar  no incident os dados do banco
    })
  },[ongId]);


  function logoff (){
    logof.push('/');
  }

  return (
    <div className="profile-container">
      <header>
        <img src={logoImg} alt="Be The Hero" />
        <span> Bem-vinda, {ongName}</span>

        <Link className="button" to="/incidents/new"> Cadastrar novo caso </Link>
        <button  type="button">
        <FiPower size={18} color="e02041" onClick={logoff} />
        </button>
      </header>
      <h1> Casos Cadastrados </h1>
      <ul>
          {incidents.map(incident =>(

            <li key={incident.id}>
              <strong> CASO: </strong>
              <p> {incident.title} </p>

              <strong> DESCRIÇÃO: </strong>
              <p> {incident.description} </p>

              <strong> VALOR: </strong>
              <p> {Intl.NumberFormat('pt-BR', {style : ' currency', currency: 'BRL'}).format( incident.value)}</p>

              <button  type="button">
                <FiTrash2 size={20} color="#a8a8b3" />
              </button>
            </li>
          ))}
      </ul>
    </div>
  )
}
